// Generate a specific curriculum structure matching target lectures and hours
const generateMatchingCurriculum = (targetLectures: number, targetHours: number, sectionCount: number): CourseSection[] => {
  const lecturesPerSection = Math.floor(targetLectures / sectionCount);
  const remainderLectures = targetLectures % sectionCount;

  // Convert target hours to total minutes for calculation
  const totalTargetMinutes = targetHours * 60;
  const averageLectureDuration = totalTargetMinutes / targetLectures; // in minutes

  const sections: CourseSection[] = [];

  for (let i = 0; i < sectionCount; i++) {
    const sectionLectures: CourseLecture[] = [];
    // Distribute remainder lectures to first few sections
    const count = i < remainderLectures ? lecturesPerSection + 1 : lecturesPerSection;

    // Define exact topics for the Advanced Web Dev course
    let sectionTitle = "";
    let topicList: string[] = [];

    if (i === 0) { sectionTitle = "Section 1: React 18 & Vite Ecosystem"; topicList = sectionTopics.react; }
    else if (i === 1) { sectionTitle = "Section 2: Next.js 14 App Router"; topicList = sectionTopics.next; }
    else if (i === 2) { sectionTitle = "Section 3: Vue.js 3 & Composition API"; topicList = sectionTopics.vue; }
    else if (i === 3) { sectionTitle = "Section 4: Full Stack with Nuxt.js"; topicList = sectionTopics.nuxt; }
    else if (i === 4) { sectionTitle = "Section 5: Enterprise Backend with NestJS"; topicList = sectionTopics.nest; }
    else if (i === 5) { sectionTitle = "Section 6: Capstone Project"; topicList = sectionTopics.capstone; }

    for (let j = 0; j < count; j++) {
      // Pick a topic from the list, or loop if we run out
      const topicIndex = j % topicList.length;
      const topicName = topicList[topicIndex];
      const lectureNumber = j + 1;

      // Vary duration slightly around the average
      const variation = (Math.random() - 0.5) * 6; // +/- 3 minutes
      let minutes = Math.floor(averageLectureDuration + variation);
      if (minutes < 3) minutes = 3;

      const seconds = Math.floor(Math.random() * 60);
      const duration = `${minutes}:${seconds.toString().padStart(2, '0')}`;

      sectionLectures.push({
        title: `Lecture ${lectureNumber}: ${topicName}`,
        duration: duration,
        preview: i === 0 && j < 3
      });
    }

    sections.push({
      title: sectionTitle,
      lectures: sectionLectures
    });
  }

  return sections;
};
const generatePrice = (basePrice: number): { price: number; originalPrice: number } => {
  // Generate random variation
  const variation = Math.floor(Math.random() * 5000) - 2500;
  const price = basePrice + variation;
  const originalPrice = Math.round(price * (1.3 + Math.random() * 0.5));
  return { price, originalPrice };
};

// Generate realistic student counts (max 100k)
const generateStudents = (popularity: 'high' | 'medium' | 'low'): number => {
  switch (popularity) {
    case 'high':
      return Math.floor(45000 + Math.random() * 55000); // 45k-100k
    case 'medium':
      return Math.floor(15000 + Math.random() * 30000); // 15k-45k
    case 'low':
      return Math.floor(3000 + Math.random() * 12000); // 3k-15k
  }
};

// Generate realistic review counts
const generateReviews = (students: number): number => {
  const reviewRate = 0.08 + Math.random() * 0.07; // 8-15% leave reviews
  return Math.floor(students * reviewRate);
};

