// Calculate precise course stats from curriculum data
const calculateCourseStats = (course: Course): Course => {
  let totalSeconds = 0;
  let totalLectures = 0;

  if (course.curriculum) {
    course.curriculum.forEach(section => {
      if (section.lectures) {
        section.lectures.forEach(lecture => {
          totalLectures++;
          if (lecture.duration) {
            const parts = lecture.duration.split(':').map(p => parseInt(p, 10));
            if (parts.length === 2) {
              // MM:SS
              totalSeconds += parts[0] * 60 + parts[1];
            } else if (parts.length === 3) {
              // HH:MM:SS
              totalSeconds += parts[0] * 3600 + parts[1] * 60 + parts[2];
            }
          }
        });
      }
    });
  }

  // Calculate hours and minutes
  const hours = Math.floor(totalSeconds / 3600);
  const minutes = Math.floor((totalSeconds % 3600) / 60);

  // Format duration string
  let durationString = '';
  if (hours > 0) {
    durationString = minutes > 0 ? `${hours}h ${minutes}m` : `${hours} hours`;
  } else {
    durationString = `${minutes} min`;
  }

  // Calculate total hours (rounded for display if needed, but keeping accurate derived data)
  const totalHours = Math.round(totalSeconds / 3600);

  return {
    ...course,
    lectures: totalLectures,
    totalHours: totalHours,
    duration: durationString
  };
};

export const courses: Course[] = rawCourses.map(calculateCourseStats);

// Export utility functions
export const getCourseById = (id: string) => courses.find(c => c.id === id);
export const getCourseBySlug = (slug: string) => courses.find(c => c.slug === slug);
export const getCoursesByCategory = (category: string) => courses.filter(c => c.category === category);
export const getCoursesByInstructor = (instructorId: string) => courses.filter(c => c.instructorId === instructorId);
export const getFeaturedCourses = () => courses.filter(c => c.featured);
export const getBestsellerCourses = () => courses.filter(c => c.bestseller);
export const getAllCategories = () => [...new Set(courses.map(c => c.category))];
export const getCategories = getAllCategories;
export const getCoursesCount = () => courses.length;
export const getTotalStudents = () => 100000;
export const getTotalHours = () => courses.reduce((sum, c) => sum + c.totalHours, 0);
export const getCategoryStats = () => {
  const categories = getAllCategories();
  return categories.map(cat => {
    const categoryCourses = courses.filter(c => c.category === cat);
    return {
      name: cat,
      count: categoryCourses.length,
      students: categoryCourses.reduce((sum, c) => sum + c.studentsEnrolled, 0)
    };
  });
};
